css:
@-moz-document url-prefix("http://www.258ch.com") {
body{background: url(http://ww2.sinaimg.cn/large/abb9363cgw1edaqtpoqurj21hc0xctua.jpg);background-size: 100% ;background-attachment: fixed;font-family: 微软雅黑;}
#category_,#connectlike{display:none}
#wp{box-shadow:0 0 35px rgba(0,0,0,0.7)}

#wp,.bm.lk{background:rgba(255,255,255,0.6);border-radius: 0 0 0 0}

.fl .bm_h_left,.fl .bm_h{background:rgba(255,255,255,0.9)}
#nv li.left{display:none}
#nv li.a{background:rgba(255,255,255,6)}
#nv li.a{box-shadow:0 10px 0 rgba(0,0,0,0.7)}
.bmw .bm_h a{color:#868686}

.bm_h{;opacity:0.66 ;-webkit-transition: opacity 600ms;transition: opacity 600ms;}
.bm_h:hover{;opacity:1;-webkit-transition: opacity 600ms;transition: opacity 600ms;}
}

@-moz-document url-prefix("http://www.258ch.com/thread"), url-prefix("http://www.258ch.com/forum.php?mod=") {
.pl .quote{background:rgba(255,255,255,0.7)}
.pls{background:rgba(255,255,255,0.1);border-width:0 0 0}
.bm{background: rgba(255, 255, 255, 0.1);}
#f_pst .tedt{background:rgba(255,255,255,0.1)}
.tedt .area{background:rgba(255,255,255,0.1)}
#fastpostmessage{background:rgba(255,255,255,0.1)}
.psth{opacity:0.7 ;-webkit-transition: opacity 600ms;transition: opacity 600ms;}
.psth:hover{opacity:1;-webkit-transition: opacity 600ms;transition: opacity 600ms;}
.a_pt{display:none}
.rs_main{display:none}
.bm {border:0}
.bbs {border-bottom:0!important}
.ad .pls{height: 2px;background:#E1E4E6;}
}

@-moz-document url-prefix("http://www.258ch.com/forum") {
.bdl{background:rgba(255,255,255,0.1)}
.bdl dl.a {background:rgba(255,255,255,0.1)}
.bdl dt {background:rgba(255,255,255,0.5)}
.bm{background:rgba(255,255,255,0.1)}
}

@-moz-document url("http://www.258ch.com/"), url("http://www.258ch.com/forum.php") {
a img {opacity:0.66 ;-webkit-transition: opacity 600ms;transition: opacity 600ms;}
a img:hover{opacity:1;-webkit-transition: opacity 600ms;transition: opacity 600ms;}
}
