css:
@-moz-document url-prefix("https://irccloud.com"), url-prefix("https://www.irccloud.com"), url-prefix("https://alpha.irccloud.com") {
	div#sidebarwrapper div#sidebar {
        display:none !important;
    }
	div#sidebarwrapper {
        width:10px !important;
    }
    div#mainwrapper {
        padding-right:5px !important;
        padding-left:5px !important;
    }

    div#sidebarwrapper:hover div#sidebar  {
        display:block !important;
    }
    div#sidebarwrapper:hover {
        width:201px !important;
    }
    div#sidebarwrapper:hover + div#mainwrapper {
        width:auto !important;
        padding-right:201px !important;
    }
}
