css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cn.morningstar.com") {
    *{color: #C7EDCC ;}
    
    span{color: #C7EDCC ;}
    
    body{background-color: #151515 !important;}

    .appBody.areaOuter.clearfix{background-color: #151515 !important;}
    .appTop.areaOuter.clearfix{background-color: #151515 !important;}
    .appNav.areaOuter.clearfix{background-color: #555555 !important;}
    .areaOuter.clearfix{background-color: #151515 !important;}
    .areaOuter{background-color: #151515 !important;}
    
    th{background-color: #151515 !important;}
    
    table{background-color: #151515 !important;}
    
    div#top{background-color: #151515 !important;}
    div.open{background-color: #555555 !important;}
    div.close{background-color: #555555 !important;}
    
    input{background-color: #888888 !important;}
    
    a{color: #C7EDFF !important;background-color: #151515 !important;}
    a.tabactive{background-color: #888888 !important;}
    a.tabinactive{background-color: #555555 !important;}
}
