css:
@-moz-document url-prefix("http://www.yahoo.co.jp/") {
/* 右のFlash広告 */
div#brandpanel
	{display:none !important;}

/* 右下 */
div#criteorb,
div#ultra
	{display:none !important;}

/* 中央 */
div#tct,
div#bpComposite,
div#commerce
	{display:none !important;}

/* 左メニュー下の情報 */
div#companybox
	{display:none !important;}
}

@-moz-document url-prefix("http://dic.yahoo.co.jp/"), url-prefix("http://dic.search.yahoo.co.jp/") {
/* Yahoo! Japan 辞書 右サイドバー */
div#pos-lrec,
div.sub-box
	{display:none}

/* 右サイドバー お知らせ */
div#kb-news1,
div#kb-news2,
div#y-news
	{display:block}

/* 上広告 */
div.compo
	{display:none}

/* 下お得情報 */
div#dic-sult
	{display:none}

/* 結果 上右下の広告 */
div.Sol,
div.bd
	{display:none}
}
