css:
@-moz-document domain(tumblr.com) {
A.new_post_label > I {height: 90px}
.new_post .new_post_label/*_icon*/ > I {
background: url('http://i47.tinypic.com/33c8bht.jpg') !important; 
}


.new_post_label > I {
  color: transparent !important;
  transition: background-position .08s linear !important;
}
#new_post_label_text > I {background-position: 2px -10px !important}
#new_post_label_photo > I {background-position: -73px -10px !important}
#new_post_label_quote > I {background-position: -148px -10px !important}
#new_post_label_link > I {background-position: -223px -10px  !important}
#new_post_label_chat > I {background-position: -298px -10px !important}
#new_post_label_audio > I {background-position: -373px -10px !important}
#new_post_label_video > I {background-position: -448px -10px  !important}

#new_post_label_text:hover > I {background-position: 2px -14px !important}
#new_post_label_photo:hover > I {background-position: -73px -14px !important}
#new_post_label_quote:hover > I {background-position: -148px -14px !important}
#new_post_label_link:hover > I {background-position: -223px -14px  !important}
#new_post_label_chat:hover > I {background-position: -298px -14px !important}
#new_post_label_audio:hover > I {background-position: -373px -14px  !important}
#new_post_label_video:hover > I {background-position: -448px -14px  !important}

#new_post_label_text:active > I {background-position: 2px -10px !important}
#new_post_label_photo:active > I {background-position: -73px -10px !important}
#new_post_label_quote:active > I {background-position: -148px -10px !important}
#new_post_label_link:active > I {background-position: -223px -10px !important}
#new_post_label_chat:active > I {background-position: -298px -10px !important}
#new_post_label_audio:active > I {background-position: -373px -10px !important}
#new_post_label_video:active > I {background-position: -448px -10px !important}

}
