css:
@-moz-document domain("stackexchange.com"), domain("stackoverflow.com"), domain("serverfault.com"), domain("superuser.com"), domain("askubuntu.com") {
noscript, #herobox-mini, .no-answers, #hmenus, #hot-network-questions, #feed-link, .topbar, .tagged, #footer,
.user-gravatar32, .user-gravatar48, .anonymous-gravatar,
.bottom-notice, #hlogo .cbt, #sidebar, #post-form, .nav-global, #custom-header, .newsletter-anon, .comments-link, .user-details span, #header
{ display: none !important; }
#content, #question-header { /*.container*/
    width: 750px !important;
    margin-top: 15px !important;
}
html, body, #content, .container {
    background: none #fff !important;
    border: none !important;
    box-shadow: none !important;
}
.post-taglist {
    opacity: 0.4;
}
.fw .vt { /* share / improve this question text */
    opacity: 0;
}
.fw .user-action-time, .fw .user-action-time span, .fw .user-action-time a {
    color: #999 !important;
}

}
