css:
@-moz-document domain("moodle.aau.dk") {
/* Course List */

#semester_category_header {
  display:none;
}

#myrooms_container {
  display:table;
  width:100%;
  padding-bottom: 0;
}

.semester_category {
  display: table-footer-group !important;
}
    
#myroomstextfilter {
  display:none;
}
  
.block_myrooms,
.block_myrooms .content {
  padding-bottom: 0 !important;
  margin-bottom: 0 !important;
}



/* Clickable Course Boxes */

.teacher_info {
  margin-top: -30px;
  pointer-events: none;
}
  
.coursebox {
  padding: 0 !important;
  border: 0 !important;
}
  
.coursebox .title a {
  border: 1px dotted #ddd;
  -webkit-border-radius: 4px;
  -moz-border-radius: 4px;
  border-radius: 4px;
  display: block;
  padding: 5px;
  padding-bottom: 25px;
  text-decoration: underline;
}

.coursebox:hover {
  background-color: #eee;
}

.coursebox .title a:hover {
  color: #444;
}



/* Breadcrumbs */

.breadcrumb-button {
  opacity: 0.3;
}
  
.breadcrumb li:nth-child(2),
.breadcrumb li:nth-child(3) {  
  display:none;
}



/* Top menus & Login */

.dropdown-toggle {
  display: none !important;
}
  
.globalnav {
  min-height:0 !important;
  height: auto !important;
  margin-left: auto !important;
  margin-right: auto !important;
  width: 50% !important;
  float: none !important;
}

.row-fluid .globalnav:nth-child(2) {
  display:none;
}
  
.aalborg_nav_wrapper {
  display:none;
}

.carousel-indicators {
  display:none;
}

  
  
/* Calendar */

.minicalendar td.hasevent.today {
  background-color: #FF9E6E;
}

  
  
/* Background */

.bg-img {
  display:none;
}
  
body.www-moodle-aau-dk {
  background-color: #272727!important;
}

  
  
/* Course */

.aalborg_title {
  display:none;
}

.summary a, .section a {
  text-decoration: underline !important;
}
}
