css:
@-moz-document domain(hitbox.tv) {

/* ===[ Main/Home Page ]===
   ================================================== */
div.homeContent {
    background: rgb(16, 16, 16)!important;
    background-image: rgb(16, 16, 16)!important;
    color: rgb(252, 252, 252)!important;
}
.footer .ng-scope a { color: white }
button,
.btn {
    background-color: rgb(176, 255, 0)!important;
    color: hsl(0,0%,20%);
}
button,
.btn:hover { background-color: rgb(115, 195, 0)!important }
.homeContent > section {
    background: rgb(16, 16, 16)!important;
    background-image: rgb(16, 16, 16)!important;
    color: rgb(252, 252, 252)!important;
}
.homeContent h1 { color: rgb(115, 195, 0)!important }
.t1 .tName,
.t1 .tStatus,
.t1 .tMetaSmall { color: rgb(176, 255, 0)!important }
/* ===[ Side Bar Stuff ]===
   ================================================== */
.subNav { background-color: rgb(16, 16, 16)!important }
.subNav h7,
h4 { color: rgb(115, 195, 0)!important }
.subNav .search input { color: rgb(115, 195, 0)!important }
.subNav .search .head { color: rgb(115, 195, 0)!important }
.subNav a { color: rgb(176, 255, 0)!important }
.subNav a:hover { color: rgb(115, 195, 0)!important }
.t3 .tStatus,
.t3 .tMetaSmall,
.t3 .tMetaSmall { color: rgb(176, 255, 0)!important }
.t3 .tName { color: rgb(115, 195, 0)!important }
/* ===[ Inbox Style ]===
   ================================================== */
.settingsContent { 
  background: rgb(16, 16, 16)!important  
}
.settingsContent h1 { color: rgb(176, 255, 0)!important }
/* ===[ Details Style ]===
   ================================================== */
button,
.btn,
input[type=submit] { color: hsl(0,0%,20%) }
.settingsContent h2,
.settingsContent .sectionTitle h3 { color: rgb(176, 255, 0)!important }
input, select, textarea { color: gray !important;}
/* ===[ Stats Style ]===
   ================================================== */
.settingsContent .chartWrap .head { color: rgb(176, 255, 0)!important }
/* ===[ Watch/Profile ]===
   ================================================== */
.userDescription div,
.userDescription span,
.userDescription li,
.userDescription h1,
.userDescription h2,
.userDescription h3,
.userDescription h4,
.userDescription h5,
.userDescription h6,
.userDescription p { color: rgb(115, 195, 0)!important }
div.chatUsers, div.detailInner { background: rgb(16,16,16)!important }
div.chatUsers > div.ng-scope > ul > li > a { color: rgb(202,202,202)!important }
div.chatUsers > div.ng-scope > ul > li > a:hover { color: rgb(100,202,100)!important }
.detail .meta { background: rgb(16,16,16)!important }
.detail .btns,
.detail .btns .active { color: white !important }
.userContent {
    border-top: solid white 1px;
    background: rgb(16,16,16)!important;
}
.profile a { color: rgb(176, 255, 0)!important }
.profile a:hover { color: rgb(115, 195, 0)!important }
.btnBlue {
    background-color: rgb(176, 255, 0)!important;
    color: hsl(0,0%,20%)!important;
    border-color: none !important;
}
.btnBlue:hover,
.btnBlueHover:hover,
.btnHoverBlue:hover {
    background-color: rgb(115, 195, 0)!important;
    border-color: none !important;
}
.detail .meta .title { color: rgb(176, 255, 0)!important }
.detail .meta .viewers:before,
.detail .meta .viewers { color: rgb(176, 255, 0)!important }
.userCover .name { color: rgb(115, 195, 0)!important }
/* ===[ Chat Style ]===
   ================================================== */
ul.chatBody { background: rgb(16, 16, 16)!important }
.chatBody .message,
.chatBody .bufferTimestamp div { color: white!important }
.chatBody li.admin { background: none !important }
.chatBody li.user,
.chatBody .name,
.chatBody .bufferTimestamp { background: none !important }
.chatSettings {
    background-color: rgb(16, 16, 16)!important;
    box-shadow: transparent !important;
}
.chatSettings .fontSize,
.chatSettings section>span { color: white }
.checkbox input[type=checkbox]:checked + label:before { color: rgb(115, 195, 0)!important }
.chatInput .textareaWrap { background: rgb(16, 16, 16)!important }
input,
select,
textarea { color: white }
.chatInput { background-color: rgb(16,16,16)!important }
.chatUsers h5 { color: white!important }
}
