css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.phoronix.com/scan.php?page=news_item") {
    div { /* Main Text Color */
        color: #00A000 !important;
    }
    a { /* Links */
        color: #E85848 !important;
    }
    h1 { /* Article Title */
        color: #DBA716 !important;
    }
    
    body,
    td,
    div { /* Main Post */
        border: 0 !important;
        border-radius: 0 0 0 0 !important;
        background: black !important;
    }
    
    .phxcms_navigation_hover_box a {
        color: #508ED8 !important;
        background: black !important;
    }
    .phxcms_navigation_hover_box a:hover {
        color: red !important;
        background: black !important;
    }
}
