css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goclio.com") {

.cliov2 header{
background-color: #333333;
padding: 2px;
}

.gradient{display:none;}

.logo-and-firm-name h1{
color: #666666;
padding: 0px;
margin: 0px;

}

#gem{

/* 
display:none;
visibility:hidden;  
*/
/* IE 8 */
  -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=30)";

  /* IE 5-7 */
  /* filter: alpha(opacity=30); */

  /* Netscape */
  /* -moz-opacity: 0.3; */

  /* Safari 1.x */
  -khtml-opacity: 0.3;

  /* Good browsers */
  opacity: 0.3;
}

.cliov2 nav{
background-color: #666666;
padding: 2px;
}

.cliov2 nav ul li a:hover{
color: #81BEF7;
}

.cliov2 header #header-controls a {
color:#81BEF7;
}

#subnav{
padding:1px;

}

.cliov2 #subnav ul li a{
padding:2px;
margin: 0px;
text-transform:uppercase;
font-weight: bold;
letter-spacing: .06em;
line-height: 16px; 
}

.cliov2 #subnav .current a{
color:#555555;
text-shadow: 0px 0px 14px #ffffff;
}


.cliov2 #subnav ul li a:hover{
color:#81BEF7;
text-shadow: 0px 0px 14px #fff;
}

.cliov2 nav ul .current a {
text-shadow: 0px 0px 14px #fff;
}

.odd{
background-color: #EEEEEE;
}

.odd.row-hover:hover{
background-color: red;
}

.dt-overdue{
background-color:#FFEFFF;
}

#flash_notice{
background-color: rgb(61, 123, 186);
border: 1px solid black;
}

.flash_notice{
background-color: rgb(61, 123, 186);
border: 1px solid black;
}

.dtv2 .filters  li a {
color: rgb(58, 122, 189);
}

.dtv2 .filters li.floatleft, .dtv2 .filters li.floatright {
color: rgb(58, 122, 189);
}

.cliov2 header #globalsearch #query{
height:33px;
padding: 2px;
margin: 0px;
/* width: 15em; */
}

.ui-widget-header{
background-color: #81BEF7;
color: black;
}
    
.banner.warn .banner_message {
background-color: #81BEF7;
color: black;
}

/* END STYLE */
}
