css:
@-moz-document domain('fimfiction.net') {
    .comment_list, #comment_preview {
        border-radius: 5px;
        border-width: 1px;
        border-style: solid;
        border-right: 1px solid #BEBAB4;
        border-color: #D6D1CB #BEBAB4 #BEBAB4 #D6D1CB;
        border-image: none;
        overflow: hidden;
    }
    #comment_preview .comment {
        padding-bottom: 25px;
    }
    .quote_container {
        box-shadow: 5px 5px 12px rgba(0, 0, 0, 0.3);
    }
    .comment_list .comment,
    .quote_container,
    #comment_preview .comment {
        position: relative;
        transition: background 0.25s linear;
        background: rgb(253, 253, 253);
    }
    .quote_container {
        border: 1px solid #CCC7C4 !important;
    }
    .comment_list .comment > div[id^="comment_content"],
    .quote_container > div[id^="comment_content"],
    #comment_preview > div[id^="comment_content"] {
        min-height: 200px;
    }
    .comment_quote_link + span:not(.spoiler) {
        display: block !important;
        padding-top: 20px;
    }
    @media all and (min-width: 700px) {
        .comment_list {
            background-color: rgb(232, 232, 232) !important;
        }
        .comment_list .comment:not(.expanded_comment),
        #comment_preview .comment {
            margin-left: 150px;
            overflow: visible !important;
        }
        .comment_list .comment .comment_information,
        .quote_container .comment_information,
        #comment_preview .comment .comment_information {
            display: inline-block;
            width: 100%;
        }
        .comment_list .comment .data,
        .quote_container .data,
        #comment_preview .comment .data {
            margin-top: -10px !important;
            margin-left: 0px;
        }
        .comment_list .comment .author,
        .quote_container .author,
        #comment_preview .comment .author {
            border-right: 1px solid rgb(204, 204, 204) !important;
            text-align: center !important;
            position: absolute;
            top: 0px;
            bottom: 0px;
            left: 0px;
            padding: 10px 10px 0px 5px;
            width: 150px !important;
        }
        .comment_list .comment .author,
        #comment_preview .comment .author {
            left: -150px;
        }
        .comment_list .comment:not(.comment_selected) .author,
        .quote_container:not(.comment_selected) .author,
        #comment_preview .comment:not(.comment_selected) .author {
            background: rgb(232, 232, 232) !important;
        }
        .comment_list .comment:nth-child(2n+1):not(.comment_selected) .author,
        .quote_container:nth-child(2n+1):not(.comment_selected) .author,
        #comment_preview .comment:not(.comment_selected) .author {
            background: rgb(238, 238, 238) !important;
        }
        .comment_list .comment:nth-child(2n):not(.comment_selected),
        .quote_container:nth-child(2n):not(.comment_selected) {
            background: rgb(240, 240, 240) !important;
        }
        .comment_selected:before,
        .comment_selected:after,
        .comment_selected {
            background: rgb(243, 243, 255) !important;
        }
        .comment_selected .author {
            background: rgb(232, 232, 255) !important;
        }
        .comment_selected:nth-child(2n+1) .author {
            background: rgb(238, 238, 255) !important;
        }
        
        .message_content {
            padding: 8px;
            color: rgb(68, 68, 68);
            border: 1px solid rgb(204, 204, 204);
            font-size: 1.1em;
            font-family: 'Segoe UI',Arial;
            -moz-box-sizing: border-box;
            outline: medium none;
            transition: border-color 0.25s ease 0s, background-color 0.25s ease 0s;
            box-shadow: 0px 0px 12px rgba(0, 0, 0, 0.07) inset;
            background-color: rgb(255, 255, 250);
            text-shadow: 1px 1px rgba(255, 255, 255, 0.8);
            vertical-align: middle;
            min-height: 120px;
            border-radius: 0px !important;
            margin-top: 25px;
        }
        .message_content .message-header,
        .message_content .date {
            margin-top: -35px;
        }
        .message_content .message-header {
            border: none !important;
        }
    }
    .comment .comment_arrow, .message_content .arrow {
        display: none;
    }
    .comment .data {
        background: none !important;
        border: none !important;
    }
    .comment_information {
        border: none !important;
    }
    .comment_information .comment_callbacks a:before {
        font-family: 'FontAwesome';
        font-size: 15px;
        content: "ÔÅµ";;
    }
    .comment_information .comment_callbacks img {
        display: none;
    }
    .comment_information .comment_callbacks a:hover {
        text-decoration: none !important;
    }
    .comment_selected .comment_data {
        box-shadow: 0px 0px 15px rgba(0, 0, 0, 0.07) inset,
            0 0 20px rgba(200, 200, 255, 0.6) !important;
    }
    .comment .comment_data {
        padding: 8px;
        color: rgb(68, 68, 68);
        border: 1px solid rgb(204, 204, 204);
        font-family: 'Segoe UI',Arial;
        -moz-box-sizing: border-box;
        outline: medium none;
        transition: all 0.25s ease 0s;
        box-shadow: 0px 0px 12px rgba(0, 0, 0, 0.07) inset;
        background-color: rgb(255, 255, 250);
        text-shadow: 1px 1px rgba(255, 255, 255, 0.8);
        vertical-align: middle;
        min-height: 120px;
    }
    blockquote {
        border: 1px solid rgb(204, 204, 204) !important;
        border-radius: 3px !important;
        background-color: rgb(255, 255, 250) !important;
        box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.07),
            0px 0px 12px rgba(0, 0, 0, 0.07) inset !important;
    }
    .comment .comment_data {
        width: 100%;
    }
    .comment .textarea_padding {
        padding-left: 0px !important;
        padding-right: 0px !important;
    }
    .comment textarea {
        padding: 8px;
        color: rgb(68, 68, 68);
        border: 1px solid rgb(204, 204, 204);
        -moz-box-sizing: border-box;
        outline: medium none;
        transition: border-color 0.25s ease 0s, background-color 0.25s ease 0s;
        box-shadow: 0px 0px 12px rgba(0, 0, 0, 0.07) inset;
        background-color: rgb(255, 255, 250);
        text-shadow: 1px 1px rgba(244, 244, 244, 0.8);
        vertical-align: middle;
        min-height: 120px;
        border-radius: 0px !important;
        width: 100%;
        resize: vertical;
        left: 0px !important;
    }
    .comment textarea:focus {
        background-color: rgb(232, 239, 246) !important;
        border-color: rgba(0, 0, 0, 0.2) !important;
        text-shadow: none !important;
    }
}
