css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("phoronix.com") {

body
{
background: #000!important;
}

td#phxcms_content_left a:hover li
{
background: #222!important;
}

.phxcms_header_legacy, .phxcms_nav_header
{
background: #333!important;
color: #aaa !important;
border: 0px !important;
}

.phxcms_bar_align, #phxcms_right_menu a, .phxcms_bar_all, .phxcms_bar_all a, #phxcms_right_menu, .phxcms_navigation_hover_box, .phxcms_navigation_hover_box a
{
background: #444!important;
color: #aaa !important;
border: 0px !important;
}

#phxcms_right_menu li
{
border: 0px !important;
}

.phxcms_bar_align a
{
color: #5a5 !important;
}

.phxcms_page_sub
{
color: #999!important;
}

#phxcms_content_phx, #phxcms_content_left, #phxcms_part_body tr
{
background: #222!important;
color: #aaa !important;
border: 0px !important;
}

#phxcms_content_left li
{
background: #222!important;
color: #aaa !important;
border: 0px !important;
}

#phxcms_content_left li:hover
{
color: #5a5 !important;
}

#phxcms_title_picture_area
{
visibility: hidden!important;
height: 0px!important;
}
}
