@-moz-document domain("facebook.com") {




body, #contentCol, #pageFooter{
	background-color: #cfc !important;
}

.UFIComment, .fbTimelineFeedbackActions, .fbTimelineFeedbackHeader, .UFIRow {
	background-color: #cdc !important;
}

#blueBar {
    border-bottom: 1px solid #050 !important;
}

#blueBar.fixed_elem{
	background-color: #373 !important;
}

