css:
@-moz-document domain("woafor.wacken.com"), domain("forum.wacken.com") {

/* pics */
div[id*="post_message"] img {max-width: 498px !important;}

/* quoted pics */
div[id*="post_message"] td.alt2 img {max-width: 444px !important;}

/* boxes */
div[id*="post_message"] pre.alt2, div[id*="post_message"] div.alt2 {max-width: 444px !important;}

/* quoted boxes */
div[id*="post_message"] td.alt2 pre.alt2, div[id*="post_message"] td.alt2 div.alt2 {max-width: 390px !important;}

/* threadreview pics */
div#collapseobj_threadreview .tborder td.alt1 img {max-width: 548px !important;}

/* threadreview quoted pics */
div#collapseobj_threadreview .tborder td.alt1 td.alt2 img {max-width: 494px !important;}

/* threadreview boxes */
div#collapseobj_threadreview .tborder td.alt1 pre.alt2, div#collapseobj_threadreview .tborder .alt1 div.alt2 {
max-width: 494px !important;}

/* threadreview quoted boxes */
div#collapseobj_threadreview .tborder td.alt1 td.alt2 pre.alt2, div#collapseobj_threadreview .tborder .alt1 td.alt2 div.alt2 {
max-width: 440px !important;}

/* preview image */
table.tborder[width="100%"]:not([id*="post"]) td.alt1 img:not(.inlineimg) {max-width: 686px !important;}

/* preview quoted image */
table.tborder[width="100%"]:not([id*="post"]) td.alt1 td.alt2 img:not(.inlineimg) {max-width: 632px !important;}

/* preview boxes */
table.tborder[width="100%"]:not([id*="post"]) td.alt1 pre.alt2, table.tborder[width="100%"]:not([id*="post"]) td.alt1 div.alt2 {max-width: 632px !important;}

/* preview quoted boxes */
table.tborder[width="100%"]:not([id*="post"]) td.alt1 td.alt2 pre.alt2, table.tborder[width="100%"]:not([id*="post"]) td.alt1 td.alt2 div.alt2 {max-width: 578px !important;}

}
