@-moz-document domain("irccloud.com") {
  .inputcell { padding: 0 !important; }
  .inputcell textarea:focus  {
    box-shadow: inset 0 0 3px blue;
  }
  .inputcell textarea {
    padding: 5px 0 !important;
  }
  div.log .gist .gist-file .gist-data {
    max-height: 500px !important;
  }
}
