css:
@-moz-document domain("55ch.org"){

*:not(#create-postform){
   font-size: 14px !important;
}
body{
   background-image: none !important;
   background-color: #1D1F21 !important;
   color: #C5C8C6 !important;
}

.reply, .bg2 > table:nth-child(13){
   color: #C5C8C6 !important;
   background-color: #282A2E !important;
   border: none !important;
}

.navbar{
   color: #C5C8C6 !important;
}

.reply {
   padding-bottom: 5px !important;
}

.postername{
   color: #B87B7D !important;
   font-weight: 500 !important;
}

.unkfunc{
   color: #B5BD68 !important;
}

.filesize{
   margin-right: 10px !important;
}

a{
   color: #5F89AC !important;
}

a:hover{
   color: #81A2BE !important;
}

hr{
   border-top: 1px solid #282a2e !important;
}

.logo{
   color: #C5C8C6 !important;
   font-size: 24px !important; 
}
.highlight{
    background-color: #1d1d21 !important;
    border: 1px solid #111 !important;
    color: #C5C8C6 !important;
}


/* CLEANING LIKE A BAWS */
.bg2 > center:nth-child(5) > p:nth-child(1) > b:nth-child(1), .replymode, .postarea > hr:nth-child(5), .replymode, .footer, .contador > div:nth-child(2), .doubledash{
   display: none !important;
}
}
