css:
@-moz-document domain("vutales.com") {
.Logo {
	background: url(http://i.imgur.com/LLG81ec.png) no-repeat bottom;
    background-position: 0px -30px;
	margin: 0px auto 15px auto;
}
body {
	background: url(http://i.imgur.com/d6Uo7RS.png);
}

.mainCol .innerContent .mainTitle {
	visibility: hidden;
        }
.mainCol .innerContent .mainTitle:before {
  
	content:"Many Tales";
	visibility: visible;
	color: rgb(186,159,51);
	font-weight: normal;
	display: block;
	font-size: 2em;
	-webkit-margin-before: 0.67em;
	-webkit-margin-after: 0.67em;
	-webkit-margin-start: 0px;
	-webkit-margin-end: 0px;
        }
h1,.forumHeaderTopic {
  color: rgb(186,159,51);
}
h2, h2 a,.forumHeaderSubtitle, .forumHeader .forumHeaderSubtitle a {
  color: rgb(229,203,122) !important;
}
.blogIcon {
  color: black;
    border: rgba(229,203,122,.5) 1px solid;
  }
.blogS1,.blogS2,.blogS3,.blogS4 {
  background: white;
  }
img {
  max-height:100%;
  max-width:100%;
  
}
.messageCentreNo a {
  color: #6e6342;
  }

}
