css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("respawn.jeuxvideo.com") {
    #table-liste-topic-forum td {
        padding: 2px;
    }
    
    body {
        font-family:'Arial';
        background: url('http://image.jeuxvideo.com/css_img/defaut/body_bg2.png') repeat-x scroll 0px 0px #D7D7D7;
    }
    
    #table-liste-topic-forum {
        font-family:'Tahoma';
        font-size: 85%;
    }
    
    img {
        vertical-align: 0;
    }
    
    #header-top {
        background: url('http://image.jeuxvideo.com/css_img/defaut/header/logo_bg.png') repeat-x scroll 0px 0px transparent;
    }
    
    #header-bottom {
        background: url('http://image.jeuxvideo.com/css_img/defaut/header/logo_bg.png') repeat-x scroll 0px 0px transparent; 
    }
    .bloc-message-forum-large .bloc-date-msg {
        float: right;
    }
    .txt-sujet-topic {
        color: #333;
    }
    .bloc-header-form {
        background: #333
    }
}
