#player.watch-medium,
#player.watch-medium-540,
#player.watch-large {
    background: #1B1B1B !important;
    width: 100% !important;
    margin: 0 !important;
}
.watch-medium #player-api,
.watch-medium-540 #player-api,
.watch-large #player-api {
    box-shadow: 0 0 32px 7px rgba(255, 255, 255, 0.22) !important;
}
.watch-medium .player-height,
.watch-medium-540 .player-height,
.watch-large .player-height {
    height: 0 !important;
    padding-bottom: 46% !important;
    position: relative !important;
}
.watch-medium .player-width,
.watch-medium-540 .player-width,
.watch-large .player-width {
    max-width: 1680px !important;
    min-width: 960px !important;
    width: 100% !important;
}
.watch-medium .player-api object, .watch-medium .player-api embed,
.watch-medium-540 .player-api object, .watch-medium-540 .player-api embed,
.watch-large .player-api object, .watch-large .player-api embed {
	position: absolute !important;
}
#theater-background { display: none; }
}
