css:
@namespace url(http://www.w3.org/1999/xhtml);

/* AGENT_SHEET */

@-moz-document domain("demotivation.me"),
regexp("http://translate\\.googleusercontent\\.com/translate.+demotivation\\.me.*") {

html {background-color: black !important;}

body {
background-color: #111 !important;
color: #999 !important;
position: relative !important;
}

.cmnt *,
.cmnt{
background-color: transparent !important;
color: #999 !important;
}

a,
.cmnt a {color: #66c !important;}

input[type='button'], 
input[type='submit'],
input[type='text'],
select,
textarea
{
-moz-appearance: none !important;
background-color: #222 !important;
color: #ccc !important;
border: 1px solid #ccc !important;
}

input[type='button'], 
input[type='submit']
{
-moz-appearance: none !important;
background-image: linear-gradient(#555,#111) !important;
border: 1px solid #ccc !important;
border-radius: 3px !important;}

HTML > BODY > NOINDEX > DIV {display: none !important;}

A[href="http://radarurl.com/monitor"] {
opacity: .3 !important;
position: absolute !important;
}

}
