@namespace url(http://www.w3.org/1999/xhtml);

/* ==== GitHub - USERSCRIPT INSTALL ICON  - FULL TITLES (Untrunctated) ==== */

@-moz-document domain("github.com") {

a#raw-url[href$=".user.js"]:after {
content: "(Install)" !important;
padding-left: 5px !important;
color: peru !important;
}
/*a:visited#raw-url[href$=".user.js"]:after {
content: "(Install)" !important;
padding-left: 5px !important;
color: red !important;
}*/
/* USERSCRIPT - ADD INSTALL ICON -
 a.js-directory-link:not([href$=".md"]):before ,
a[href$=".user.js"]:before ,
=== */
a#raw-url[href$=".user.js"]:before {
    -moz-transform: scale(0.9, 0.9) !important;
    content: url("http://userscripts-mirror.org/images/script_icon.png") !important;
    display: inline-block !important;
    height: 18px !important;
    width: 10px !important;
    vertical-align : middle !important;
    margin-top : -2px !important;
    padding : 1px 8px 1px 4px  !important;
border-radius: 3px !important;
background-color: green !important;
}
