css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.ifanr.com/"), url-prefix("http://www.ifanr.com/category/special") {

#cat-list,#recommend-wrap,#dasheng-index{display:none;}
.entry-meta,.entry-content,.live-post-excerpt,.live-post-img{display:none;}

.live-post-cat{height:22px;width:36px;padding: 0 3px;}
.live-post-entry{margin-left:30px;margin-top:-35px;}

.live-post-inner{border:none;background-color:#fff;padding: 40px 18px 15px;}

.entry-live-hover .live-post-inner{background-color:#fff;}
.JS_index_live{margin-bottom:-20px;margin-top:-20px;font-weight:normal;}
.entry-name,.live-post-title{font-weight:normal;}
}

@-moz-document url("http://www.ifanr.com/dasheng") {

.main-dasheng{width:980px;margin:0;}
.main-dasheng .dasheng_content blockquote{width:820px;font-size:15px;}
}

@-moz-document regexp("^http://www\\.ifanr\\.com/[0-9]+$") {
#JS_WBLike,.author-profile,.single-nav,#comments-box,#recommend-wrap{display:none;}
div#recommend-wrap{display:none;}
}

@-moz-document domain("www.ifanr.com") {
.main{width:980px;}
#header,footer,.content-header{display:none;}
#sbl{display:none;}
}
