@-moz-document domain("instagram.com") {
.top-bar {
  position: relative;
  height: 43px;
  border-bottom: 1px solid #c4c4c4;
  border-bottom-color: rgba(4, 40, 71, 0.8);
  -webkit-box-shadow: 0 1px 0 rgba(111, 151, 182, 0.5) inset, 0 -1px 0 rgba(111, 151, 182, 0.2) inset, 0px 1px 1px rgba(0,0,0,.3);
  -moz-box-shadow: 0 1px 0 rgba(111, 151, 182, 0.5) inset, 0 -1px 0 rgba(111, 151, 182, 0.2) inset, 0px 1px 1px rgba(0,0,0,.3);
  box-shadow: 0 1px 0 rgba(111, 151, 182, 0.5) inset, 0 -1px 0 rgba(111, 151, 182, 0.2) inset, 0px 1px 1px rgba(0,0,0,.3);
  background-color: #d1d1d1;
  background-image: url("http://d36xtkk24g8jdx.cloudfront.net/bluebar/c851db0/images/shared/noise-1.png"), -webkit-gradient(linear, left top, left bottom, from(#d1d1d1), to(#a1a1a1)) !important;
  background-image: url("http://d36xtkk24g8jdx.cloudfront.net/bluebar/c851db0/images/shared/noise-1.png"), -webkit-linear-gradient(top, rgb(50, 50, 50), rgb(20, 20, 20)) !important;
  background-image: url("http://d36xtkk24g8jdx.cloudfront.net/bluebar/c851db0/images/shared/noise-1.png"), -moz-linear-gradient(top, rgb(50, 50, 50), rgb(20, 20, 20)) !important;
  background-image: url("http://d36xtkk24g8jdx.cloudfront.net/bluebar/c851db0/images/shared/noise-1.png"), -o-linear-gradient(top, rgb(50, 50, 50), rgb(20, 20, 20)) !important;
  background-image: url("http://d36xtkk24g8jdx.cloudfront.net/bluebar/c851db0/images/shared/noise-1.png"), -ms-linear-gradient(top, rgb(50, 50, 50), rgb(20, 20, 20)) !important;
  background-image: url("http://d36xtkk24g8jdx.cloudfront.net/bluebar/c851db0/images/shared/noise-1.png"), linear-gradient(top, rgb(50, 50, 50), rgb(20, 20, 20)) !important;
  background-position: 50% 50%;
  z-index: 100;
  }
}
