css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(forum.bioware.com) {
  /* Notification settings */
  .notification_table .desc { font-size: 8pt!important }
  /* Status-update comment */
  .list_content,
  /* Status-update comment input */
  .input_text,
  /* Forum post, quote */
  .post_body .post,
  /* Forum posts on profile page */
  #profile_panes_wrap .post p,
  /* Post previews */
  #post_preview,
  /* Thread starter, reply count, group quick-menu */
  .ipsType_small, .hentry,
  /* Thread header */
  .ipsBox_withphoto > .desc { font-size: 10pt }
  /* Thread name in listing */
  .__topic a.topic_title,
  /* PM name in listing */
  .col_m_subject a { font-size: 11pt }
  .maintitle, .post_wrap > h3 { background-color: #929292!important; }
  .post.entry-content p, .status_content,  #post_preview { color: #000000; }
  .post.entry-content .ipsBlockquote p { color: #666666; }
}
