css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zenphoto.org") {

head, body, #top, #container, #footer, #wrapper, .threadauthor, .alt,
fieldset, #terms_of_service, .postform {
background: #000 !important;
}
#mainnav, #mainnav li, #mainnav li a, #mainnav li a:hover, .slide ,
 .buttons a, .table_of_content_list, .articlebox, .articlebox-left,
.buttonzip a, .buttontar a, .infobox, .infobox-3columns, .notebox,
#latest, #latest th, #forumlist th, #favorites th, .threadpost
{
background: #444 !important;
border: none !important;
}
#latest td, #forumlist, .bb-root td:hover, #forumlist tr:hover
{
background: #444 !important;
border:1px #FFF solid!important;
}
#mainnav li a:hover, .buttons a:hover
{
background: #222 !important;
}
#latest td a:hover
{
text-decoration: underline !important;
}
a, p, b, h1, h2, h3, h4, em, .articledate, .rsslinks li, label,
#sidebar li, .inlinecode, #entrybody, .downloadlinks li, legend,
#latest td, #latest th, #forumlist th, #favorites th, .poststuff,
#terms_of_service, .required, #forumlist, code
{
color: #FFF !important;
}
}
