css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pixiv.net") {

/* 
Vertical alignment fix for "Illustrations" caption
for Latin characters(?) 
*/

._type-slim-header body {
font-size: inherit !important;
}

/* Content wrapper */
._type-slim-header #wrapper {
margin-top: 1em !important;
}

/* Header container */
._type-slim-header ._header.ui-fixed {
position: static !important;
}

/* Header wrapper */
._type-slim-header ._header .layout-wrapper {
padding-right: 0 !important;
width: auto !important;
}

/* Logo and Illustrations menu container */
._type-slim-header ._header .navigation-container {
right: auto !important;
left: 10px !important;
width: auto !important;
}

/* Illustrations Menu */
._type-slim-header ._header .category-container > .current {
width: auto !important;
}

/* search */
._type-slim-header ._header .search-container{
position: static !important;
}

/** Remove **/

/* Iframe Advertisement */
#header-banner.ad {
display: none !important;
}

}
