 /* AGENT_SHEET */ 
 /* SCROLLBAR */

scrollbar thumb {-moz-appearance: none !important;
    background: #555 !important;
    border:none!important;}
scrollbar thumb:hover {background: #444 !important;}

scrollbar[orient=vertical] {width:4px !important;}
scrollbar[orient="horizontal"] {height: 4px !important;}

scrollbar thumb:active, scrollbar thumb:focus {background: #0088FF !important;}


/* hide scrollbar up/down buttons */
scrollbar scrollbarbutton {visibility: collapse !important;}



scrollbar thumb, textbox scrollbar thumb {
border-radius: 0px !important;

}
