css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.netcarshow.com") {
    .tPx, .tP, .wD, .wA, .mTm, .mT, .tL input{display:none;}
    a[title='NetCarShow.com']{display:none;}
    a {font-size:1.5em;line-height:1.5em;text-shadow:1px 1px black;}
    div.Pi a{font-size:1em;line-height:1em;}
    .wW {float:none;}
    .wFr {float:none; padding:0px 20px 0px 20px;}
    div.wTt{display:block;position:relative;left:-5px;top:-20px;z-index:-99;}
    div.wT{position:absolute;}
    #iH div:first-child{position:absolute;top:0px;z-index:-1;}
    #iH div:first-child span{display:none;}
    .lb{font-size:1em;margin-left:100px;}
    #iH div:last-child{position:absolute;top:10px;z-index:1;}
}
