css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
    
/* remove the following from youtube: 
comments
bottom youtube logo
links to blogs, about, partners, etc
*/ 

    #watch-discussion,
    #footer-main,
    #footer-links, 
    #footer-links-primary,
    #footer-links-secondary,
    #appbar-guide-button,
    #watch7-action-panel-footer,
    #search-btn,
    #guide-container,
    #appbar-guide-menu {
    display:none !important; 
    }

/* remove padding from bottom. change color */ 

    #body-container {
       padding-bottom: 0px !important;
       background: #fff !important;
    }
    
    /* borders are not allowed */ 
    #yt-masthead-container,
    #watch7-main,
    #watch7-content,
    #watch7-user-header,
    #watch7-headline,
    #watch7-action-buttons,
    #appbar-guide-menu { 
        border: none !important; 
        background: #fff !important; 
    }
    
    #appbar-nav { 
        border: none !important; 
        text-align: left !important; 
        padding-left: 131px !important; 
    }
    
    
    /* related video sidebar */
    
    #watch7-sidebar-contents { 
         margin-top: 0px !important;
         background: #fff;  
    }
    
    /* actual video */ 
    
    #player { 
        padding-top: 0px !important; 
    }
    
    /* buttons */ 
   
    #watch7-secondary-actions .yt-uix-button.yt-uix-button-toggled,
    #watch7-secondary-actions .yt-uix-button:hover, 
    #watch7-secondary-actions .yt-uix-button:active {
        color: #1b1b1b;
        border-bottom: 3px solid #222222;
    }
    
    #watch-dislike.yt-uix-button {
        display: none !important; 
    }
    
    #masthead-search { 
        width: 300px; 
    }
    
    .yt-masthead-logo-container { 
        width: 80px !important;
    }
    
    #logo { 
        margin-left: -13px; 
    }
    
    #page {
        padding-left: 0px !important;
     }
    
    #yt-masthead-container {
        box-shadow: 0px 0px 2px 2px rgba(20,20,20,0.3) !important; 
    }
}
