css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.studivz.net") {
  body { background: #ccccff !important; color: #000000 !important; }
  ul { color: #000000 !important; }
  div#leftAds, div#righAds { background: transparent !important; }
  #logo { display: none !important;  }
  #pageHeader { background: #6666aa !important; border: 1px #6666aa solid !important; border-top: 1px #ffffff dashed !important; }
  #content { border: 1px #6666aa solid !important; background: #ddddff !important; }
  #content div { background: transparent !important; border-color: #000088 !important; }
  .linkList { border-width: 1px 1px 0 1px !important; border-color: #6666aa !important; border-style: solid !important; }
  .linkList li { border-bottom: 1px #6666aa solid !important; background: #ddddff !important; }
  a { color: #000088 !important; }
  a:active { color: #000088; background: #eeeeff !important; }
  .linkList a:hover { background: #ffffff !important; color: #000088 !important; }
  #topHeader { background-image: none !important; background: #6666aa !important; -moz-border-radius: 8px 8px 0 0 !important; }
  #topHeader a { color: #ffffff !important; }
  #topHeader a:hover { background: #8888aa !important; }
  h2 { background: #8888aa !important; border-width: 0 0 1px 0 !important; border-style: solid !important; border-color: #000088 !important; color: #ffffff !important; }
  h3 { color: #000088 !important; }
  .microblogInfo, .friendsInfo, .pinboard_Navi, .commentMetaData, #pageFooter, div.mH { background: #bbbbdd !important; border-color: #000088 !important; color: #000000 !important; }
  #pageFooter { margin-top: 4px !important; }
  input { border-color: #0000aa !important; background: #eeeeff !important; }
  #Kds { border-color: #000088 !important; border-style: dashed !important; border-width: 0 0 1px 1px !important; }
  ul#tabBar { border-color: #000088 !important; }
  ul#tabBar li a:hover, ul#tabBar li a:active { background: #bbbbdd !important; }
  ul#tabBar li.selected a { background: #8888aa !important; color: #ffffff !important; }
  div.buttonArea input, input.fieldBtnSubmit { background: #000088 !important; color: #ffffff !important; width: auto !important; border-width: 1px !important; border-color: #aaaaff #000022 #000022 #aaaaff !important; }
  fieldset { border-color: #000088 !important; }
  ul.tcl li { border-color: #000088 !important; }
  ul.tcl li ul li { border-right: 1px #000088 solid !important; }
}
