css:
@-moz-document domain("saltybet.com"), domain("twitch.tv") {
#videoEmbed {
display:none !important;
}

#sbettorswrapper {
  width:50% !important;
}

#chatWrapper {
  width:50% !important;
}

#twitch_chat {
max-width: none !important;
}

body,#chat{
background-color: rgb(15, 15, 15) !important;
color: rgb(187, 187, 187) !important;
}

tr.odd > td:nth-child(1){
background-color: rgb(115, 117, 120) !important;
}

tr.even > td:nth-child(1){
background-color: rgb(65, 67, 70) !important;
}

tr.even{
background-color: rgb(65, 67, 70) !important;
}

tr.odd{
background-color: rgb(115, 117, 120) !important;
}

#landing{
background-color: rgb(65, 65, 65) !important;
}

#signinform{
background: rgb(115, 117, 120) !important;
}

#indexlogo{
display: none !important;
}

#status{
background: linear-gradient(rgb(65, 65, 65),#252525) !important;
color: rgb(187, 187, 187) !important;
}

#chat_text_input{
color: rgb(187, 187, 187) !important;
background-color: rgb(15, 15, 15) !important;
}
}
