css:
/*===================================================
  akizukidenshi.com ja jp liquid compact 2014/03-
  ===================================================*/

@-moz-document domain("akizukidenshi.com"){
/*===================================================
  akizukidenshi.com - global
  domain(akizukidenshi.com)
  ===================================================*/
/*--- unwanteds ---*/

/*--- generic containers ---*/
div, table, tbody, tr, td {box-sizing: border-box; -moz-box-sizing: border-box;}
table, tbody, tr, td {
 display: block; float: left;
 width: auto !important; height: auto !important;
 }
body {font-family: inherit !important;}
#wrapper {width: auto !important;}

/*--- header ---*/
#header {width: auto !important;}
#header h1 a img {height: 1em !important; width: auto !important;}
#header tr:first-child td:last-child table tr:first-child br {display: none !important;}

/*--- footer ---*/
#freespace03 {width: auto !important; margin: 0 !important;}
body>#wrapper>#freespace03>table>tbody>tr {width: 100% !important;}
body>#wrapper>#freespace03>table>tbody>tr:first-child>td
 {width: 33.33% !important;}
body>#wrapper>#freespace03>table>tbody>tr:first-child>td>table
 {width: 100% !important;}


/*--- site footer ---*/
#footer {width: auto !important; padding: 0 !important;}
}

@-moz-document url-prefix("http://akizukidenshi.com/catalog/goods/search"){
/*===================================================
  akizukidenshi.com catalog serach result
  url-prefix(http://akizukidenshi.com/catalog/goods/search)
  ===================================================*/
/*--- unwanteds ---*/

/*--- main contents search result (2columns) ---*/ 
body>#wrapper>table {width: 100% !important;}
td.leftmenuframe_ {position: relative !important; width: 180px !important;}
td.mainframe_
 {float: right !important; width: 100% !important; margin: 0 0 0 -180px !important;}
td.mainframe_>*
 {width: auto !important; margin: 0 0 0 180px !important;}
#maincontents2 {float: none !important;}
#hyouji {width: auto !important;}
}

@-moz-document url("http://akizukidenshi.com/catalog/default.aspx"), url("http://akizukidenshi.com/catalog/"){
/*==================================================
  akizukidenshi.com - top page
  url(http://akizukidenshi.com/catalog/default.aspx)
  ==================================================*/
/*--- unwanteds ---*/

/*--- main contents search result (3columns) ---*/
body>#wrapper>table {width: 100% !important;}
body>#wrapper>table>tbody>tr>td.tdkakomi:first-child
 {position: relative !important; width: 180px !important; z-index: 9;}
body>#wrapper>table>tbody>tr>td.tdkakomi:first-child div
 {width: 100% !important;}
body>#wrapper>table>tbody>tr>td.tdkakomi:last-child
 {position: relative !important; width: 200px !important;}
body>#wrapper>table>tbody>tr>td.tdkakomi:nth-child(2) {
 position: relative !important;
 width: 100% !important; margin: 0 -200px 0 -180px !important;
 }
body>#wrapper>table>tbody>tr>td.tdkakomi:nth-child(2)>div
 {width: auto !important; margin: 0 200px 0 180px !important;}
body>#wrapper>table>tbody>tr>td.tdkakomi:nth-child(2)>div a img
 {width: 100% !important;}
}

@-moz-document regexp("https?:\/\/akizukidenshi\.com\/catalog\/[a-z]\/[a-z][a-z]+.*"){
/*======================================================================
  catalog browing page (2columns layout)
  regexp('https?:\/\/akizukidenshi\.com\/catalog\/[a-z]\/[a-z][a-z]+.*')
  =====================================================================*/
#wrapper>table>tbody>tr>td:first-child
 {position: relative !important; z-index: 9; width: 180px !important;}
#wrapper>table>tbody>tr>td.mainframe_
 {position: relative !important; width: 100% !important; margin: 0 0 0 -180px !important;}
#wrapper>table>tbody>tr>td.mainframe_>*
 {width: auto !important; margin: 0 0 0 180px !important;}


/*=================================================
  product detail page
  =================================================*/
/*--- unwanteds ---*/

/*--- contents ---*/
#maincontents, .kanren {width: auto !important;}
#maincontents>br {display: none !important;}
.order_g {margin: 0 !important; padding: 0 !important;}
.kanren {clear: both; margin: 0 !important;}
.kanren h2 { color: blue !important; background: none !important;}
.order_b {margin: 0 !important; padding: 0 !important;}
.order_b * {font-size: 1em !important;}
.order_b br+br {display: none !important;}
}

@-moz-document url("https://akizukidenshi.com/catalog/customer/menu.aspx"){
/*=============================================
  akizukidenshi.com login page
  url(https://akizukidenshi.com/catalog/customer/menu.aspx)
  =============================================*/
/*--- unwanteds ---*/

/*--- login form --*/
#mypage{width: auto !important;}
h3 {margin: 0 !important; padding: 0 !important;}
#mypage>br {display: none !important;}
#mypage>table, #mypage>table tbody {width: 100% !important;}

#mypage>br+table tr:first-child, #mypage>br+table tr:first-child *
 {width: 100% !important;}
#mypage>br+table tr:nth-child(2) td:last-child {display: none !important;}
#mypage>br+table tr:nth-child(3) td:last-child {display: none !important;}
#mypage>br+table tr:nth-child(4) {float: right !important;}
#mypage>br+table tr:nth-child(4)>td>input {height: 2.5em;}
#mypage>br+table tr:nth-child(4) td:first-child {display: none !important;}
#mypage>br+table tr:last-child {display: none !important;}

#mypage>table+table tr:first-child, #mypage>table+table tr:first-child *
 {width: 100% !important;}
#mypage>table+table tr:nth-child(2) {float: right !important;}
#mypage>table+table tr:nth-child(2) td:nth-child(odd) {display: none !important;}
#mypage>table+table tr:nth-child(2) td:nth-child(2) input {height: 2.5em;}
#mypage>table+table tr:nth-child(3) {display: none !important;}

#mypage>font {font-size: 1em !important;}
}
