css:
@-moz-document url-prefix("http://auctions.search.yahoo.co.jp/search"){
/*=================================================
  yafuoku ja jp search result lequid compact 2014/03
  url-prefix(http://auctions.search.yahoo.co.jp/search)
  =================================================*/

/*--- unwanteds ---*/
#mIn>[id^='So'].ss,
.lcSize180>[id^='So'].ss,
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
div {box-sizing: border-box;}
#SaB {width: auto !important; height: auto !important; margin: 0 !important;}
#SaA, #SaA-dtl {min-height: auto !important; margin: 0 !important;}
#contents, #SaA-dtl, #ygma, #Se, #Sq .inner, #Sf {width: auto !important;}
#SaC {float: none !important; min-height: auto !important; margin: 0 !important;}
#Sf2 .inner
 {width: auto !important; min-height: auto !important; padding: 0 !important;}
#SaD a {margin-left: 0.5em !important;}
#Sq {margin: 0 !important;}
#Sq .inner {padding: 0 !important;}
#SqC, #SqD, #SqA {margin: 0 !important;}
#SqB {padding: 0 !important;}
#Sf .inner {width: auto !important; min-height: auto !important; padding: 0 !important;}

/*--- header / footer ---*/
#header {min-width: auto !important; margin: 0 !important;}
.normalHeader {height: auto !important;}
.normalHeader #SaB {margin: 0 !important;}
#Sf2 .loginStats {padding-left: 0.5em !important;}
#Sf h1.t {padding: 0 !important; display: inline !important;}
#Sf .resultNum {padding: 0 !important;}

/*--- contents 2columns layout ---*/
.uWrap {max-width: 100%;}
.uWrap>.lcSize180 {position: relative !important; z-index: 9;}
.uWrap>.rcSize100per {
 position: relative !important; float: right !important;
 width: 100% !important; margin: 0 0 0 -180px !important;
 }
.uWrap>.rcSize100per>div {width: auto !important; margin: 0 0 0 180px !important;}

}
