css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imprensafalsa.com") {
    body,
    div
    {
        background: black !important;
        color: #00A000;
        font-family: serif !important;
    }
    
    div.anunciotop,
    #navigation,
    #extras,
    #footer,
    div.links,div.widgets
    {
        display: none !important;
    }
    
    #posts .date,
    #posts .post .sign .author{
        color: #508ED8 !important;
    }
    
    div.post,
    #comentar {
        width: 850px !important;
        margin: 0 auto !important;
    }
    
    div.post > h3.title {
        margin-bottom: 30px !important;
        text-align: center !important;
    }
    div.post > h3.title > a {
        color: #DBA716 !important;
    }
    #posts .navbot {
        text-align: center !important;
    }
    #posts .navbot > a {
        color: #E85848;
    }
    .clinks > a,div.postreply > a
    {
        color: red !important;
    }
    div.author {
        text-align: right;
    }
    
    #comentar .title > a {
        font-size: 125%;
        font-weight: bold;
        color: #508ED8;
    }
    #comentar .title > a:hover {
        font-size: 125%;
        font-weight: bold;
        text-decoration: none;
    }
    
    #blogtitle, #blogdesc,
    #blogtitle a
    {
        color: white !important;
    }
    #blogtitle a:hover {
        color: grey !important;
    }
    
    #blogtitle, #blogdesc,
    #posts
    {
        width: 100%;
        text-align: center !important;
    }
    #container,
    .header
    {
        width: 100% !important;
    }
    div.header { height: auto !important; }
    #sub-navigation { height: 60px; }
}
