css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix("http://www.app-sales.net/"),
url-prefix("http://app-sales.net/")
{

body {
  color: #eee;
  background: #0F0F0F !important;
  background-image: url("https://img-fotki.yandex.ru/get/4123/161026817.0/0_acd93_38f25aaa_orig") !important;
  background-attachment: fixed !important;
  background-size: cover !important;
}

h1, h2, h3 {
  color: #AAA !important;
  text-shadow: 1px 1px 1px #000 !important;
}

#headerwrapper {
  background: rgba(0,0,0,0.6) !important;
}

#contentwrapper {
  background: none !important;
}

#footerwrapper {
  background: rgba(0,0,0,0.85) !important;
}

#bottombar {
  border-top: 1px solid #252525 !important;
  background: #050505 !important;
}

.sales tr {
  background: rgba(0,0,0,0.7) !important;
}

.sales tr:not(.nosale):hover, .sales tr:focus {
  background: rgba(0,0,0,0.9) !important;
}

.sales {
  color: #aaa !important;
}


#mostwanted .slider .slide.active {
  background: none !important;
}


#mostwanted .slider .slide {
  background: none !important;
}


.viewmore a {
  background: rgba(12,12,12,.4) !important;
  color: #bbb !important;
}

.viewmore a:focus, .viewmore a:hover {
  background: rgba(12,12,12,.6) !important;
}

h1 a:hover, h1 a.active {
  color: #eee !important;
  text-shadow: 2px 2px 1px #000 !important;
}

#detail #content, .big-info {
  background: rgba(0,0,0,0.4) !important;
}

.tickLabel {
  color: #bbb !important;
}

}
