css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("oregonlive.com"){
  #adv-network, #adv_network, #adv-classified-nav, #adv-full, #adv-section-nav, #adv-subsection-nav, .ck-region-pre-content,
  .ck-block-about-this-feature, .ck-region-header, .masthead, header#adv_header, div.ck-actions.ck-clearfix, #section_title {
    display: none !important;}
  #main_well {width: 100%;}
  #news-river {max-width: 970px;}
  #news-river, #news-river div.wrapper {margin: 0;}
  .ck-block-comic a.ck-toggle-comments, #main-nav, nav.nav-primary, iframe#dsq-2, .ck-fb-frame, 
  .ck-recommended-comics, .ck-region-footer, #footer, #disqus_thread, #column3,
  aside.IndexSidebar, #best_of {visibility: collapse;}
  body {overflow: hidden;}
}
