css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google.com/search)	{

/* gets rid of all the stuff you don't need */
#header, #ssb, #mbEnd, #footer, .slk, nobr, .fl, .hd, #tads, #np, .nr, #nc, #nn, #nf, font, .a	{	display: none !important	}

#nav td		{	display: none	}

/* configures the results div */
#res	{ 
	position: absolute;
	top: 10px;
	bottom: 40px;
	left: 10px;
	padding: 0px 10px 10px 10px !important;
	overflow-y:scroll;
	overflow-x:hidden;
	width: 200px;
	background-color: grey;
	-moz-border-radius: 10px ;
	}

img { width: 180px !important;
height: auto !important; }

/* applies the style */
body 	{	background: #262626 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAENCAYAAADOoA9GAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABZSURBVHjaYmBgYPjPACP+MYFIEPEflfUflwQRSojgUiyGX4IkJTTRi5P4R39T/tFByT8qa6OJof8GPJgGPjHQI/FTki+pWFCQXlQhsZiBmBFMwLlgwAgQYAAYPe0bQ81rlgAAAABJRU5ErkJggg==") top left repeat-x !important;	}

a	{	
	color: darkred !important;
	text-decoration: none !important; 
	}

a:hover, a:active, a:visited:hover	{	color: red !important	}

a:visited	{	color: green !important;	}

.b	{
	display:block !important;
	position: fixed;
	bottom: 10px !important;
	height: 20px;
	width: 80px !important;
	background-color: grey;
	-moz-border-radius: 10px;
	}

#nav tbody tr > td:first-child	{	left: 10px !important;	}

#nav tbody tr > td:last-child	{	left: 150px !important;	}	

.b:hover	{	background: darkgrey !important;	}
}
