css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("asiandvdclub.org") {

.main, .news, .news-body, .box, .statstable td, .navigation,
.even td, .odd td, .description, .description td, .text, .pm,
.alternate, table.staffpagetable td, .embedded td,
.main td, .toolbar, blockquote, .comment
{
background:#850001 !important;
}
.comm-heading, th,
div.bbcode_area button.bbcode,
div.alertbar, div.pm, div.comment_div
{
background: #FF0000 !important;
}
.poll .rowA
{
background: #333333 !important;
}
.poll .rowB
{
background: #222222 !important;
}
.poll .myvote
{
background: #444444 !important;
}
.notification 
{
background-color: #FF0000 !important;
}
.tool_btn, .dropdown, input, textarea
{
font-size: 17px !important;
padding: 2px 5px !important;
background: #000000 !important;
border: 1px #FF0000 solid !important;
border-radius: 5px !important;
}
input.modern
{
background:#FF0000 !important;
border:none !important;
}
blockquote
{
border: 1px #FFFFFF solid !important;
}
div.pm, div.pm-container, div.news, div.box,
div.comment_div
{
border: 1px #FF0000 solid !important;
}
#brdstats, #brdfooter
{
border-top: 1px #FF0000 solid !important;
}
.pun tr
{
border-bottom: 1px #FF0000 solid !important;
}
div.comment
{
border: 2px  #FF0000 solid !important;
}
table.clean td, table.clean th
{
border: 2px  #FF0000 solid !important;
}
.even td, .odd td, div.bbcode_quote table tr td,
div.bbcode_area button.bbcode, div.navigation,
table.alternate tr td, td, th, div.outer
{
border-color: #FF0000 !important;
}
div.toolbar
{
border-color: #850001 !important;
}
div.bbcode_area button.bbcode
{
box-shadow: none !important;
}
a, h1, h2, h3, h4, h5, p, ul, li, td, .news-heading, .box, .dvd5-sized, .mw800, input, textarea, th, legend, u,
.batch-sized, .size, .zero-month, .dls, .leechers, .comms
{
color:#FFFFFF !important;
}
.logo-text, .logo-text a, .logo-text apan
{
color:#000000 !important;
}
.user_heading, .r10
{
color:#FFFF00 !important;
}
.green
{
color:#00FF00 !important;
}
.bluray, .blue
{
color:#0089FD !important;
}
a:hover
{
text-decoration:underline !important;
}
}
