css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mwo.smurfy-net.de") {
.label-mech-hardpoint-missle {
color: rgb(255, 255, 255);
}
.slot {
background: none repeat scroll 0 0 #202020;
border: 1px solid #716604;
}
.component-title {
background-image: linear-gradient(to bottom, rgb(113, 102, 4), rgb(190, 171, 7));
border: 0px;
}
.component-restrictions {
background-image: linear-gradient(to bottom, rgb(190, 171, 7), rgb(113, 102, 4));
border: 0px;
}
.slot-internal {
color: rgb(105, 95, 17);
}
.slot-dynamic {
color: rgb(135, 135, 135);
}
.slot-free {
color: rgb(85, 85, 85);
}
.label-mech-hardpoint-beam {
color: rgb(255, 255, 255);
}
.label.armor-values {
background-color: rgb(70, 70, 70);
border: 0px;
}
}
