@-moz-document domain("pcmag.com") {

* {
	border: none!important;
	border: 0!important;
	box-shadow: none!important;
	outline: none!important;
	text-decoration: none!important;
}

.pros-cons-bl ul li strong,
 ul.rev-tabs li a {
    text-transform: capitalize!important;
}

/*___ Fonts __________________________________________________________________________________*/

.reviewProducts .product-price .zdcse > strong, .reviewProducts .product-price .zdcse .zdcse-free, .reviewProducts .product-price .zdcse .zdcse-too-low, 
.reviews-content-left .reviews-tabs ul.rev-tabs li a,
.article-section .article-body p, .review-body p,.slide-caption,p,.heading h2 a,
body,h1, h2, h3, h4, h5, h6,footer .copyright .footer-copy p,.affiliate-text,.pros-cons-bl ul li strong,
html {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

.tile.small h2 > a {
    font-size: 20px;
	line-height: 27px!important;
}

.pros-cons-bl ul li strong,
p {
    font-size: 1.2rem!important;
}

p {
	line-height: 27px!important;
}

footer .copyright .footer-copy p {
    font-size: 14px!important;
    line-height: 26px!important;
}

/*___ Remove Items ___________________________________________________________________________*/

[id*="outofslot-panel-container-mainPanel-"],
.column.large-fixed-300-right.show-for-large-up,
#bottomrightrail,
[id*="zergnet-widget-"],.article-share,#share_container .share_menu.vertical,
.in-content-lazy,#share_container,
#_evh-link,
.pcmwrap,.spotlight-module,
[data-specless-format="Ziff Davis: Masthead"],
[id*="container-main-canvas"],
#pcmedia,
.evidon-consent-button,
.tile.strip::after {
	position: absolute!important;
	top: -9999px!important;
	left: -9999px!important;
}

/*___ Left/Right/Top/Bottom ___________________________________________________________________*/

selector {
	left: 0!important;
}

.tile.featured .text-wrapper {
    top: calc(0rem)!important;
}

[data-specless-masthead-state="has-not-scrolled-past"][data-specless-masthead-expanded-state="expanded"] {
    margin-top: 0!important;
}

/*___ Margins _______________________________________________________________________________*/

.bottom-line-circ,
.article-section .post-body .article-body .slicker li,
.tile,#widgetbox,
.large-fluid-300-left.column,
.large-fixed-300-right.column {
	margin: 12px!important;
}

Selector {
	margin: 0 12px!important;
}

/*___ Paddings ______________________________________________________________________________*/

.bottom-line-circ,
.article-section .post-body .article-body .slicker li,
.tile,#widgetbox,
.large-fluid-300-left.column,
.large-fixed-300-right.column {
	padding: 12px!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights _______________________________________________________________________________*/

selector {
	Max-height: none!important;
}

/*___ Widths ________________________________________________________________________________*/

#widgetbox,.article-section .post-body .article-body,
.row {
	max-width: none!important;
}

.row {
    width: 98%!important;
}


#main-wrapper .wrapper .small-20.large-fluid-300-left.column {
    width: 64%!important;
}

.medium-20.large-fluid-300-left.column {
    width: 94%!important;
}

body #widgetbox #toprightrail, 
.large-fixed-300-right.column {
    width: 33% !important;
}


.flex .flex-2, .flex .lb-module {
    width: calc(49% - 15px)!important;
}







}

@-moz-document domain("pcmag.com") {
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

.article-header .deck, .review-head-title .deck, .content .article-header .deck,
.affiliate-text,
.slide-caption,
li,
p {
	color: #fff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Khaki _______________________________________________________________________________________________*/

.author-info span,
.privacy-banner .message a, .truste-messageColumn a,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Burlywood ___________________________________________________________________________________________*/

.rating-list,.reviewProducts .product-price .zdcse span,
.pcmag-buyitnow-logo,.pros-cons-bl ul li strong,
.heading h2 a,
em,i,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Sandy Brown _________________________________________________________________________________________*/
.newsletter-widget strong,.article-section .post-body a.zdcse-buybtn, .review-body a.zdcse-buybtn,
h5,
h6 {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue ________________________________________________________________________________________________*/

.slide-caption strong,
.hide-for-small-only {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green _______________________________________________________________________________________________*/

.bottom-line-circ .product-information .zdcse.zdcse-block.zdcse-buybtn.zdcse-no-paren .price,
.reviewProducts .product-price .zdcse > strong, .reviewProducts .product-price .zdcse .zdcse-free, .reviewProducts .product-price .zdcse .zdcse-too-low,
#msrp-value span,.pcmag-buyitnow-price,
div.pcmag-buyitnow-lowest-price span.pcmag-buyitnow-lowest-price-value {
	color: #0f0!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red _________________________________________________________________________________________________*/

selector {
	color: #f00!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

.a-input-text,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*________________________________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}


}

@-moz-document domain("pcmag.com") {
/*___________________________________________________________________________________________*/

html, body {
	background: #000 url(http://image.ibb.co/hYKqvw/noise_background.png)!important;
	background-attachment: fixed!important;
	width: 100%!important;
	height: 100%!important;
}

/*___ Background Gray __________________________________________________________________________*/

.commerce-scroll-header,
header#main-top-nav .trending-nav {
	background-color: #060505!important;
}

/*___ Background 0000 __________________________________________________________________________*/

.article-section .post-body a.zdcse-buybtn, .review-body a.zdcse-buybtn,
.reviews-div:not(.optimizely) .review-blender .pcmag-buyitnow table.pcmag-buyitnow-table tr td,
.article-section .post-body .article-body .slideshow-container .slide,
.article-section .post-body .article-body .content .slide-list-view,
.article-section .post-body .article-body .content,
.tile.featured .text-wrapper,.card,#main-wrapper > .wrapper,
header#main-top-nav nav .global-search-wrapper .fa.fa-search,
header#main-top-nav nav .social,
header#main-top-nav nav .main-nav {
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ______________________________________________________________________*/

.reviewProducts ul li .product-wrapper, .reviewProducts .products li .product-wrapper,
.author-container .author-stories ul li,
.author-container {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Highlight Background ________________________________________________________________________*/

selector:hover {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}

/*___ Faded Background bottom (Usually expand paragraph) _____________________________________*/

selector {
    background: linear-gradient(to bottom,rgba(255,255,255,0),rgba(120, 224, 240, 0.13))!important;
}

/*______________________________________________________________________________________________*/

selector {
	background-image: none!important;
}

/*______________________________________________________________________________________________*/

pre,
code {
	background: #111!important;
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
}

/*__ Blue ________________________________________________________________________________________*/

.toprightrail #newsletter-signup, #toprightrail #newsletter-signup, #widgetbox #newsletter-signup,
.author-container .author-stories .author-fullbio a,
.link-button {
	border-radius: 10px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #05497F, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.toprightrail #newsletter-signup, #toprightrail #newsletter-signup, #widgetbox #newsletter-signup:hover,
.author-container .author-stories .author-fullbio a:hover,
.link-button:hover {
	background-image: linear-gradient(to bottom right, #06508F, #000)!important;
	color: #ff0!important;
}

.toprightrail #newsletter-signup, #toprightrail #newsletter-signup, #widgetbox #newsletter-signup:active,
.author-container .author-stories .author-fullbio a:active,
.link-button:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red ________________________________________________________________________________________*/

a.zdcse-buybtn::after, body tr td.pcmag-buyitnow-buybutton a::after {
	border-radius: 10px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #7f0505, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

a.zdcse-buybtn::after:hover, body tr td.pcmag-buyitnow-buybutton a::after:hover {
	background-image: linear-gradient(to bottom right, #8f1616, #000)!important;
	color: #ff0!important;
}

a.zdcse-buybtn::after:active, body tr td.pcmag-buyitnow-buybutton a::after:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Yellow ________________________________________________________________________________________*/

Selector {
	color: #fff!important;
	border-radius: 10px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #956101, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #a67212, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Light Blue _________________________________________________________________________________________*/

selector {
	color: #fff!important;
	border-radius: 10px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #4d87bc, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #5e98cd, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Green _________________________________________________________________________________________*/

selector {
	color: #fff!important;
	border-radius: 10px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Plain Black ______________________________________________________________________________________*/


.large-fluid-300-left.column,#widgetbox,
.large-fixed-300-right.column {
	background-color: #000!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

.large-fluid-300-left.column:hover,#widgetbox:hover,
.large-fixed-300-right.column:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Plain ___________________________________________________________________________________________*/

.bottom-line-circ,
.reviewProducts,.slick-slide,
.article-section .post-body .article-body .slicker li,
.tile {
	background-color: #111!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

.bottom-line-circ:hover,
.reviewProducts:hover,.slick-slide:hover,
.article-section .post-body .article-body .slicker li:hover,
.tile:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Noise ___________________________________________________________________________________________*/

footer,
header#main-top-nav {
	background-image: linear-gradient(to bottom, rgba(0, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

footer:hover,
header#main-top-nav:hover {
	background-image: linear-gradient(to bottom, rgba(255, 255, 255, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}

/*__ Red Gradient ______________________________________________________________________________________*/

.reviews-content-left .reviews-tabs ul.rev-tabs li a:hover, .reviews-content-left .reviews-tabs ul.rev-tabs li a.active,
.review-ratings .editors-choice-text,#widgetbox .affiliate-text,
.privacy-banner
 {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/*__ Blue Gradient ______________________________________________________________________________________*/

selector {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

/*__ Green Gradient _____________________________________________________________________________________*/

selector {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Yellow Gradient _____________________________________________________________________________________*/

selector {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #ff9 0%, #000 100%)!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Shadow ____________________________________________________________________________________________*/

Selector {
	box-shadow: 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Red Animation __________________________________________________________________________________*/

.commerce-scroll-header .progress-bar {
  background: linear-gradient(90deg, #E20309, #000, #E20309)!important;
background-size: 400% 400%!important;

-webkit-animation: AnimationName 30s ease infinite!important;
-moz-animation: AnimationName 30s ease infinite!important;
animation: AnimationName 10s ease infinite!important;
}

@-webkit-keyframes AnimationName {
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}
@-moz-keyframes AnimationName {
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}
@keyframes AnimationName { 
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}

}

@-moz-document domain("pcmag.com") {
/*___________________________________________________________________________________________*/

.flex .flex-3,
.tile {
	filter: brightness(75%)!important;
}

/*___________________________________________________________________________________________*/

.flex .flex-3:hover,
.tile:hover {
	filter: brightness(100%)!important;
}

/*___________________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*___________________________________________________________________________________________*/

selector,
selector:first-child {
	border-top-left-radius: 22px!important;
	border-top-right-radius: 22px!important;
}

selector,
selector:last-child {
	border-bottom-left-radius: 22px!important;
	border-bottom-right-radius: 22px!important;
}

/*___________________________________________________________________________________________*/

p,
li,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

.reviews-content-left .reviews-tabs ul.rev-tabs li a.active,
p:hover,
li:hover,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

}
