css:
a:hover,a>font:hover,b>font:hover,a:hover *,button[type="submit"]:hover *,label:hover
{transition:color 0.423s ease,text-shadow 0.423s ease;color:rgba(255,255,255,1)!important;
text-shadow:0 1px rgba(64,64,255,0.9),0 1px 1px rgba(64,64,255,0.9),0 -1px 1px rgba(64,64,255,0.9),
1px 0 1px rgba(64,64,255,0.9),-1px 0 1px rgba(64,64,255,0.9),0 0 3px rgba(64,64,255,0.9)!important;}
