css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(twitter.com) {
  * {}

  .dashboard {
    display: none;
  }
    
  .wrapper {
    margin: 0 auto;
    width: auto;
    max-width: 590px;
    transition-duration: 250ms;
  }

  .content-main {
    float: none;
    width: auto;
    margin: 0 auto;
    max-width: 590px;
    transition-duration: 250ms;
  }

  .profile-card.profile-header {
    float: none;
    margin: 0 auto 20px;
    width: 590px;
    position: relative;
  }

  @media (max-width: 640px) {
    .content-main {
      border-radius: 0;
      max-width: 100%;
    }

    .content-header {
      border: none;
    }
    
    .header-inner {
      border-radius: 0;
    }

    .dashboard {
      display: none;
    }

    .wrapper {
      padding: 40px 0 0 0;
      width: auto;
    }

    .stream-item {
      border-right: none;
      border-left: none;
    }

    .stream-end {
      margin-bottom: 0;
      border-radius: 0;
    }

    .stream-end-inner {
      border-radius: 0;
    }

    .stream-loading {
      margin-bottom: 0;
      border-radius: 0;
    }
  }

}
