css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nhl.com") {

	#gameNavi {
		margin: 0 !important;
		position: relative;
		z-index: 1;
		width: 976px !important;
		box-shadow: 0 14px 12px -9px #000000 !important;
		}
	
	#miniGC .masthead {
		height: 78px !important;
		}
	
	#gameNavi .scrollable {
		width: 925px !important;
		} 
	
	#miniGC { height: 492px; }
	
	#miniGC .body { height: 490px; }
	
	/* Elements that are removed. */
	
	#qotd_module img,
	#c_mrm3,
	div .GoogleActiveViewClass,
	#deeplinks li.deeplink-button.gamecenter-link,
	#c_copyright,
	#leaderBoardAd,
	#miniGC .masthead .shield,
	#miniGC .masthead .pillsSearch,
	#langSignIn {
		display: none !important;
		}
		   
}
