css:
@-moz-document domain("news.ycombinator.com") {
  body {
    background-color: #f6f6ef;
    font-family: 'source-sans-pro','Source Sans Pro','AvenirNext-Regular','Avenir-Book','OpenSans-Light','OpenSans','Open Sans','Calibri','Helvetica Neue','Helvetica','DroidSans',sans-serif;
    font-size: 100%;
    font-style: normal;
    line-height: 1.4375;
    text-rendering: optimizeLegibility;
    -moz-font-feature-settings:"onum" 1;
    -ms-font-feature-settings:"onum" 1;
    -o-font-feature-settings:"onum" 1;
    -webkit-font-feature-settings:"onum" 1;
    font-feature-settings:"onum" 1;
  }

  table {
    line-height: 1.4375;
    max-width: 53.125em;
  }

  td {
    font-family: inherit;
    font-size: 1em;
  }

  .title {
    font-family: inherit;
    font-size: 1.5em;
  }

  .comment {
    font-family: inherit;
    font-size: 1.125em;
  }

  .comment u a, .default p u a {
    font-size: 1.5em;
    padding-bottom: 0.625em;
  }

  .default {
    font-family: inherit;
    font-size: 1em;
  }

  .subtext {
    font-family: inherit;
    font-size: 0.8125em;
    padding-bottom: 0.625em;
  }

  .comhead {
    font-family: inherit;
    font-size: 0.875em;
  }

}
