css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("php.net") {

body, .clearfix, .navbar, #layout-content, .body, td, .sub, footer,
.warning, .note, .text, .layout-menu, #header
{
background:#222 !important;
}
.clearfix, .navbar, .title, footer
{
border:none !important;
}
.title, .panel, th, .dc-description, .varname, .phpcode,
.screen, .htmlcode, .content-box pre, .block, .innerbox,
.info, .corners-top span, .corners-bottom span, #footer,
.adv-block, #main-menu
{
background:#000 !important;
}
a, p, h1, h2, h3, h4, h5, li, td, .sub, th, .headline, .personname,
.pubdate, .editedby, .year, .holder, .note, .type, .methodname,
strong, .parameter, .initializer, code, .methodsynopsis, .simpara,
code span, pre, .modifier
{
color:#FFF!important;
}
a:hover, .chunklist li a:hover
{
text-decoration:underline !important;
}
a, .chunklist li a
{
text-decoration:none !important;
border-bottom:none !important;
}

}
