css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("duckduckgo.com") {
a.header__button--menu{
    opacity:0.5;
}

.header-wrap--home{
    border-top: 4px solid #f0f0f0;
}

.search--home{
    box-shadow:0px 3px 10px #e0e0e0;
}

.tag-home{
    display:none;
}

.foot-home{
    display:none;
}

a:visited{
    color:#bebebe!important;
}

.search--home.has-text .search__button{
    background-color: #bebebe;
}

.search--home.has-text:hover .search__button:hover{
    background-color: #747474;
}

.search__button:hover{
    background-color: #747474;
}

.search:hover .search__button, .search__input:focus ~ .search__button, .search--header.has-text.search--hover .search__button, .search--header.has-text.search--focus .search__button, .search--home.has-text .search__button{
    background-color:#747474;
}

div.nav-menu--slideout.is-open{
    box-shadow:0px 0px 25px #e4e4e4;
}

.header-wrap{
    background-color: #fefefe;
    box-shadow: 0px 10px 20px #f6f6f6;
}

/*** Developed by Sarthak. If you enjoyed this style consider pledging at https://pledgie.com/campaigns/27589 ***/

}
