css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filmweb.pl") {
.headerContainer {
    background: rgba(184, 184, 184, 0.56) !important;
    padding: 5px 10px 0 !important;
    line-height: 1px !important;
}
.fbtn.fbtn-primary.fbtn-medium.userName {
background: rgba(32, 32, 32, 0.1) !important;
color: rgb(112, 4, 4) !important;
font-size: 15px !important;
font-weight: bold !important;
}
#menu a {
color: rgb(112, 4, 4) !important;
font-weight:bold !important;
}
.fbtn-primary:hover, .fbtn-primary.disabled, .fbtn-primary[disabled] {

    background: rgba(204, 24, 0, 0.58) !important;

}
.fbtn-primary {

    background: rgba(32, 32, 32, 0.1) !important;

}
#mainSearchCont .resultsList {
border: 2px solid rgb(202, 202, 202)!important;
}
.userLinks > li a {
opacity: .9 !important;
}
}
