css:
@-moz-document domain("www.torn.com") {
#banner{height:145px;}

.stats-bar-fill{height:100%!important;}
#tblInfo2{
  position:absolute;
  top: -195px;
  left: 190px;
}
.happybar,.energybar,.nervebar{background-size:25px 25px!important;}

#tblInfo2 .leftcol,#tblInfo2 .rightcol,#tblInfo2 td{
  color:white;
  text-shadow:none;
}
.stats-bar-container,.barPad{
  height:25px!important;
}
#nav-mailbox a{
  position:absolute;
  top: 77px;
  left: 4px;
}
#nav-events a{
  position:absolute;
  top: 77px;
  left: 68px;
}
#nav-awards a{
  position:absolute;
  top: 77px;
  left: 132px;
}
#nav-g3 .title{
  visibility: hidden;
  height:0px;
}
#TornLogo a img{
  content: url("http://i.imgur.com/gmNbRf3.png");
  width:170px;
  height:75px;
}
#TornLogo{
  width:170px;
  height:75px;
  margin: 0px 0px 0px 15px !important; 
}
#nav-g3 > li a{
  color:#FfFfFf;
  background: #8C9296;
  background: -webkit-gradient(linear, left top, left bottom, from(#8C9296), to(#94A7B3));
  background: -moz-linear-gradient(top, #8C9296,  #94A7B3);
  background: linear-gradient(#8C9296, #94A7B3);
  text-decoration: none;
  text-shadow: -1px -1px 0 rgba(0,0,0,0.3);
  font-size:11px;
  text-align:center;
  margin:0px;
  padding:0px;
  vertical-align:bottom;
  height:65px;
  width:60px;
  border: 1px solid #8C9296;
}
#nav-g3 > li a:hover{

  background: linear-gradient(#8C9296, #678191);
}
#nav-mail span{position: absolute;top:15px;left:6px;width:64px;height:64px;background: url('http://www.iconsdb.com/icons/download/white/email-3-48.png') no-repeat ;}
#nav-events span{position: absolute;top:15px;left:6px;width:64px;height:64px;background: url('http://www.iconsdb.com/icons/download/white/electricity-48.png') no-repeat ;}
#nav-awards span{position: absolute;top:15px;left:6px;width:64px;height:64px;background: url('http://www.iconsdb.com/icons/download/white/medal-48.png') no-repeat ;}
}
