css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stackoverflow.com") {

.container, .question-page, .categories,
.home-page, .search-page, #search
{
background:#000 !important;
}
.default, .prettyprint, .prettyprinted, .pln, .str, .com, .pun, .kwd,
.tag, code, .wmd-input, .wmd-button-bar, .ask-page, .header,
.actual-edit-overlay, input, .modal-content, .js-gps-track,
.post-text blockquote
{
background:#663800 !important;
}
.comment, .topbar-icon:hover
{
background:#000 !important;
}
.default
{
border:1px #FFF solid !important;
}
.comments-link, .post-tag, .youarehere, #search input,
#nav-tags, #nav-users, #nav-badges, #nav-unanswered,
#nav-questions, #nav-askquestion, #tabs a, .stats
{
color:#FFF !important;
background:#663800 !important;
border:1px #FFF solid !important;
}
a, p, .pln, .str, .com, .pun, .kwd, .tag, .atn, .atv, .comment-copy,
.wmd-input, .processed, .actual-edit-overlay, label,
.actual-edit-overlay, input, .mini-counts,
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td
{
color:#FFF !important;
}
}
