/* Created by Dpoc41 on 20 March 2014, last updated on 20/March/2014*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.catholic.com"),
domain("catholic.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: grey !important;
background: black !important;
}

button

{
background: grey !important;
}

div, span, a

{
background-color: black !important;
}

input, span, code, textarea, pre, div

{
color: grey !important;
}


/*links*/

a

{
color: #5285B2 !important;
}

a:visited

{
color: #287036 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
